import { Link } from "react-router-dom";
import React, { useEffect, useRef, useState } from "react";


import "../../src/utils/menu.css"

const Product = ({ product }) => {
    const textRef = useRef(null);

    useEffect(() => {
        const adjustFontSize = () => {
            const parentWidth = textRef.current.parentElement.clientWidth;
            const maxFontSize = 16; // Tama침o m치ximo de fuente
            let fontSize = maxFontSize;

            textRef.current.style.fontSize = `${fontSize}px`;

            while (textRef.current.scrollWidth > parentWidth && fontSize > 8) {
                fontSize -= 1;
                textRef.current.style.fontSize = `${fontSize}px`;
            }
        };

        adjustFontSize();
        window.addEventListener("resize", adjustFontSize);

        return () => {
            window.removeEventListener("resize", adjustFontSize);
        };
    }, [product.name]);

    return (
        <div className="product-name">
            <h3 ref={textRef}>{product.name}</h3>
        </div>
    );
};

export default Product;
