import { Link } from "react-router-dom";
import React, { useEffect, useRef, useState } from "react";


import "../../src/utils/menu.css"

const Product = ({ product }) => {
    const [isLongText, setIsLongText] = useState(false);
    const textRef = useRef(null);

    useEffect(() => {
        const adjustTextSize = () => {
            if (textRef.current) {
                const parentWidth = textRef.current.parentElement.clientWidth;
                const textWidth = textRef.current.scrollWidth;

                // Ajustar el tama침o del texto si es m치s largo que el contenedor
                if (textWidth > parentWidth) {
                    setIsLongText(true);
                } else {
                    setIsLongText(false);
                }
            }
        };

        adjustTextSize();
        window.addEventListener("resize", adjustTextSize);

        return () => {
            window.removeEventListener("resize", adjustTextSize);
        };
    }, [product.name]);

    return (
        <div className="product-name">
            <h3 ref={textRef} className={isLongText ? "long-text" : ""}>{product.name}</h3>
        </div>
    );
};

export default Product;
